import React, { useRef, useEffect, useState } from 'react';
import './Camera.css';

const Camera = () => {
  const videoRef = useRef(null);
  const [isStreaming, setIsStreaming] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video: {
            width: { ideal: 640 },
            height: { ideal: 480 },
            facingMode: 'user'
          }
        });
        
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          
          // Esperar a que el video estÃ© listo para reproducir
          videoRef.current.onloadedmetadata = () => {
            videoRef.current.play().then(() => {
              setIsStreaming(true);
              setError(null);
            }).catch((playError) => {
              console.error('Error playing video:', playError);
              setError('Error al reproducir el video de la cÃ¡mara.');
              setIsStreaming(false);
            });
          };
        }
      } catch (err) {
        console.error('Error accessing camera:', err);
        setError('No se pudo acceder a la cÃ¡mara. Verifica los permisos.');
        setIsStreaming(false);
      }
    };

    startCamera();

    return () => {
      if (videoRef.current && videoRef.current.srcObject) {
        const tracks = videoRef.current.srcObject.getTracks();
        tracks.forEach(track => track.stop());
      }
    };
  }, []);

  return (
    <div className="camera-container">
      <div className="camera-wrapper">
        <video
          ref={videoRef}
          autoPlay
          playsInline
          muted
          className="camera-video"
        />
        {!isStreaming && !error && (
          <div className="camera-loading">
            <div className="loading-spinner"></div>
            <p>Iniciando cÃ¡mara...</p>
          </div>
        )}
        {error && (
          <div className="camera-error">
            <div className="error-icon">ðŸ“·</div>
            <p>{error}</p>
            <button 
              className="retry-button" 
              onClick={() => window.location.reload()}
            >
              Reintentar
            </button>
          </div>
        )}
        {isStreaming && (
          <div className="camera-overlay">
            <div className="camera-indicator">
              <div className="recording-dot"></div>
              <span>EN VIVO</span>
            </div>
          </div>
        )}
      </div>
      <div className="camera-status">
        <div className={`status-indicator ${isStreaming ? 'active' : 'inactive'}`}></div>
        <span>{isStreaming ? 'CÃ¡mara activa' : 'CÃ¡mara inactiva'}</span>
      </div>
    </div>
  );
};

export default Camera;
