import React, { useState } from 'react';
import './App.css';
import Camera from './components/Camera';
import AttendanceStats from './components/AttendanceStats';
import EmotionStats from './components/EmotionStats';

function App() {
  const [isCameraExpanded, setIsCameraExpanded] = useState(false);

  const toggleCameraSize = () => setIsCameraExpanded(prev => !prev);

  return (
    <div className="App">
      <header className="app-header">
        <h1 className="main-title">
          Tecnologías Emergentes TN Pilar 2025
        </h1>
      </header>
      
      <main className={`main-content ${isCameraExpanded ? 'expanded' : ''}`}>
        <div className={`layout ${isCameraExpanded ? 'layout-expanded' : ''}`}>
          <section className="left-panel">
            <div className="left-panel-header">
              <button className="toggle-size-btn" onClick={toggleCameraSize}>
                {isCameraExpanded ? 'Reducir cámara' : 'Agrandar cámara'}
              </button>
            </div>
            <div className="camera-section">
              <Camera />
            </div>
          </section>

          <section className="right-panel">
            <div className="stats-section">
              <div className="stats-container">
                <AttendanceStats />
                <EmotionStats />
              </div>
            </div>
          </section>
        </div>
      </main>
    </div>
  );
}

export default App;
